	private int output = 0;

	@Override
	public void calculations() {
		
		//loop through each character in the given input and check if it's either a ( or a ) and act accordingly
		for(int i = 0; i < input.length(); i++) {
			if(input.charAt(i) == 40) {
				output++;
			} else if(input.charAt(i) == 41) {
				output--;
			}
			
			//now since part two requires us to decide when he is hitting the basement for the first time we check each iteration when the output
			//is -1
			if(output == -1) {
				//we add one here, because the assignment clearly states that the first character in the array is position one
				//and as we can see the initial value of i is 0, so we up i by the difference (which is one in this case)
				this.output(i + 1);
				//break out of the current for block
				break;
			}
		}
		
	}

	@Override
	public void output(Object out) {
		//we write our output to the console
		this.write("Day 1 part 2: " + out.toString());
		//and we kill the exercise
		this.kill();
	}

